import React, { Component } from 'react';
import TOC from "./components/TOC";
import Content from "./components/Contents";
import Subject from "./components/Subject";
import './App.css';

// 최신 자바스크립트는 클래스 안에 속하는 function은 function을 생략 원래 function render이나 생략
// 하나의 최상위 태그만 써야된다 !
// attibute == props



class App extends Component{
  // 컴포넌트 초기화시켜주고 싶은 코드를 여기다 집어넣음
  constructor(props){
    super(props);
    this.state = {
      mode:'read',
      subject:{title:"WEB" , sub:"world Wid Web!"},
      Welcome:{title:"Welcome", desc:"Hello, React!!"},
      contents:[
        {id:1, title:'HTML',desc:"HTML is HyperText"},
        {id:2, title:'CSS', desc:"CSS is for design"},
        {id:3, title:'JavaScript', desc:"JS is for for Interactive"}
      ] // 데이터가 많아 배열형태
    }
  }
  render() {
    console.log("App render")
    var _title, _desc = null;
    if(this.state.mode === 'Welcome'){
      _title = this.state.Welcome.title;
      _desc = this.state.Welcome.desc;
    }else if (this.state.mode === 'read'){
      _title = this.state.contents[0];
      _desc = this.state.contents[0];
    }
  return (
    <div className="App">
      {/*<Subject title={this.state.subject.title} sub={this.state.subject.sub}></Subject>*/}
      <header>
            <h1><a href="/" onClick={function(e){
              e.preventDefault();
 //             이벤트가 호출되었을때 this에 값이 아무값도 셋팅되지않음.
 //             this.state.mode = 'Welcome';
                this.setState({
                  mode:'Welcome'
                });
            }.bind(this)}>{this.state.subject.title}</a></h1>
            {this.state.subject.sub}
      </header>
      <TOC data={this.state.contents}></TOC>
      <Content title={_title} desc={_desc}></Content>
    </div>
    );
  }
}
export default App;
